import styled from "styled-components";

export const Container = styled.div`
  max-height: 400px;
  max-width: 230px;
  display: flex;
  align-items: center;
  flex-direction: column;
  border-radius: 10px;
  background-color: #ffffff;
  box-shadow: 0px 0px 6px rgba(47, 37, 68, 0.25);

  img {
    width: 100%;
    height: auto;
    border-radius: 10px 10px 0 0;
  }
  .details {
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    padding: 10px;

    h3 {
      font-size: 18px;
    }

    a {
      text-decoration: none;
      font-size: 12px;
    }

    p {
      font-size: 12px;
      color: #7b6490;
    }
  }
  button {
    height: 36px;
    width: 90%;
    max-width: 210px;
    border: none;
    background-color: #8c56c2;
    border-radius: 20px;
    font-size: 14px;
    font-weight: bold;
    color: #ffffff;
    margin: 20px 10px;
  }
`;
