import styled from "styled-components";

export const Container = styled.div`
  background-color: #ffffff;
  width: 40%;
  margin: 10% auto;
  height: 340px;
  border-radius: 10px;

  section {
    display: flex;
    flex-direction: row;
    padding: 40px;
    .imgContainer {
      height: 260px;
      width: 260px;
      overflow: hidden;

      img {
        height: auto;
        width: 100%;
      }
    }

    .modal__details {
      background-color: red;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
    }
  }
`;
