import styled from "styled-components";

export const Container = styled.div`
  height: 56px;
  width: 100vw;
  background-color: #ff6fff;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

  img {
    max-width: 20%;
    height: 45px;
  }
  .input__Container {
    width: 80%;
    display: flex;
    align-items: center;

    input {
      background-color: none;
      width: 100%;
      border: 1px solid #e1dfe0;
      border-radius: 4px;
    }
  }
`;
