import styled from "styled-components";

export const Container = styled.div`
  max-height: 273px;
  max-width: 230px;
  display: flex;
  flex-direction: column;
  border-radius: 10px;
  background-color: #ffffff;

  img {
    width: 100%;
    height: auto;
    border-radius: 10px 10px 0 0;
  }
  .details {
    display: flex;
    flex-direction: column;
    align-items: flex-start;

    p {
      font-size: 12px;
    }
  }
`;
