import styled from "styled-components";

export const Container = styled.div`
  height: 56px;
  width: 100vw;
  background-color: #ffffff;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  box-shadow: 5px 5px 10px black;

  img {
    max-width: 20%;
    height: 40px;
    margin-right: 30px;
  }
  .input__Container {
    width: 80%;
    display: flex;
    align-items: center;
    height: 100%;

    input {
      background-color: none;
      width: 100%;
      height: 36px;
      padding-left: 10px;
      border-radius: 4px 0 0 4px;
      border: none;
      border: 1px solid #e1dfe0;
    }
    button {
      border: none;
      background-color: #8c56c2;
      height: 40px;
      width: 36px;
      border-radius: 0 4px 4px 0;
    }
  }
  @media (max-width: 600px) {
    .logo {
      display: none;
    }
  }
`;
